package Day6;

public class AssignmentOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		  int x = 10, y = 20, z = 30; //initialization
		    x += y; 
		    y -= x + z; 
		    z *= x * y;
		 System.out.println("x = " +x ); 
		 System.out.println("y = " +y ); 
		 System.out.println("z = " +z ); 

	}

}
